import React from "react"
import './index.css'


class Game extends React.Component {


    constructor(props) {
        super()
        this.state = {
            nextGame: [],
            awayTeam: [],
            awayTeamScore: null,
            homeTeam: [],
            homeTeamScore: null
        }
    }

    async componentDidMount() {
        let url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/games.json?status=unplayed,in-progress"
        let response = await fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        });
        let data = await response.json();
        if (data.games[0].schedule.playedStatus === "LIVE") {
            url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/games/" + data.games[0].schedule.id + "/boxscore.json"
            response = await fetch(url, { 
                headers: new Headers({
                  'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
                }), 
            });
            data = await response.json(); 
            console.log("TEST");
            this.setState({nextGame: data.game})  
        }

        else {
            this.setState({nextGame: data.games[0]})
        }

        console.log(this.state.nextGame)

    }

    render() {
        return (
            <div className="game">
                <div id="awayTeam">
                    <img className="teamImg" src={process.env.PUBLIC_URL + '/assets/' + this.state.awayTeam.id + ".png"} alt=""/>
                    <p className="teamName">{this.state.awayTeam.abbreviation}</p>
                    <p className="teamScore">{this.state.awayTeamScore}</p>
                </div>
                <div id="homeTeam">
                <img className="teamImg" src={process.env.PUBLIC_URL + '/assets/' + this.state.homeTeam.id + ".png"} alt=""/>
                    <p className="teamName">{this.state.homeTeam.abbreviation}</p>
                    <p className="teamScore">{this.state.homeTeamScore}</p>
                </div>
            </div>
        );
      }



}

  export default Game
