import React from "react"
import './index.css'


class TeamGameStat extends React.Component {


    constructor(props) {
        super()
        this.state = {
            teamStats: [],
        }
    }

    async componentDidMount() {
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/team_stats_totals.json?team=" + this.props.id
        const response = await fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        });
        const data = await response.json();

        this.state = {
            teamStats: data.teamStatsTotals[0].stats
        }
        console.log(this.state.teamStats);

    }

    render() {
        let stats = this.state.teamStats
        return (
            <p className="teamName">stats.gamesPlayed</p>
        )
      }



}

