import React from "react"
import './index.css'
import Team from "./Team.js"

import { trackPromise } from 'react-promise-tracker';

class Game extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            isLoaded: false,
            nextGame: [],
            awayTeam: [],
            subText: null,
            awayTeamScore: null,
            homeTeam: [],
            homeTeamScore: null
        }
    }

    async componentDidMount() {

        trackPromise (this.getData())

    }

    getData() {
        let url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/games.json?status=unplayed,in-progress&team=TOR"
        let response =  fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        });
        let data =  response.json();
        if (data.games[0].schedule.playedStatus === "LIVE") {
            url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/games/" + data.games[0].schedule.id + "/boxscore.json"
            response = fetch(url, { 
                headers: new Headers({
                  'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
                }), 
            });
            data = response.json();
            this.setState({nextGame: data}) 
            var timeLeft = this.state.nextGame.scoring.currentPeriodSecondsRemaining; 
            var minutes = Math.floor(timeLeft/60);
            var seconds = timeLeft % 60;
            this.setState({
                awayTeam: this.state.nextGame.game.awayTeam,
                awayTeamScore: this.state.nextGame.scoring.awayScoreTotal,
                homeTeam: this.state.nextGame.game.homeTeam,
                homeTeamScore: this.state.nextGame.scoring.homeScoreTotal,
                subText: "P" + this.state.nextGame.scoring.currentPeriod + " (" + minutes + ":" + seconds + ")",
                isLoaded: true
            }) 

            this.getSubText()


        }

        else {
            this.setState({nextGame: data.games[0]})
            let date = new Date(this.state.nextGame.schedule.startTime)
            let options = { weekday: 'long', month: 'long', day: 'numeric', hour: 'numeric', minute: '2-digit'};
            this.setState({
                awayTeam: this.state.nextGame.schedule.awayTeam,
                awayTeamScore: this.state.nextGame.score.awayScoreTotal,
                homeTeam: this.state.nextGame.schedule.homeTeam,
                homeTeamScore: this.state.nextGame.score.homeScoreTotal,
                subText: date.toLocaleString('en-US', options),
                isLoaded: true
            }) 

        } 
    }

    getSubText() {
        if (this.state.nextGame.scoring.currentPeriod === null) {
            this.setState({subText: "Intermission"})
        }

        else if (this.state.nextGame.scoring.currentPeriod > 3) {
            this.setState({subText: "Period: Overtime"})
        }

        if (this.state.nextGame.game.playedStatus !== "LIVE") {
            this.setState({subText: "Final Score"})
        }
    }

    render() {

        if (this.state.isLoaded) {
            return (
                <div className="game">
                    {this.state.isLoaded && (<Team type="awayTeam" id={this.state.awayTeam.id} abbreviation={this.state.awayTeam.abbreviation} score={this.state.awayTeamScore}/>)}
                    {this.state.isLoaded && (<Team type="homeTeam" id={this.state.homeTeam.id} abbreviation={this.state.homeTeam.abbreviation} score={this.state.homeTeamScore}/>)}
                    <p className="subText">{this.state.subText}</p>
                </div>
            );
        }

        else {
            return <div></div>
        }

      }
}
  export default Game
