import React from "react"
import './index.css'


class DivisionStandings extends React.Component {


    constructor(props) {
        super()
        this.state = {
            teams: [],
            teamStat: [],
            standing: []
        }
    }

    async componentDidMount() {
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/2021-regular/standings.json?team=2,12,13,14,21,23,24";
        fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        })
        .then(res => res.json())
        .then(
            (data) => {
                this.setState({
                    teams: data.teams
                });
            }
        );
    }

    render() {
        return (
            <div className="standings">
            <h1>Standings: (North Division)</h1>
            <table>
                <tbody>
                    <tr>
                        <th>Team</th> 
                        <th>GP</th> 
                        <th>W</th> 
                        <th>L</th> 
                        <th>OTL</th> 
                        <th>Pts</th>
                        <th>GF</th> 
                        <th>GA</th> 
                        <th>Diff</th>
                    </tr>


                    {this.state.teams
                    .sort((a, b) => a.divisionRank.rank - b.divisionRank.rank)
                    .map((team,index) =>
                        <tr key={team.team.id}>
                            <td>{index}. {team.team.name}</td>
                            <td>{team.stats.gamesPlayed}</td>
                            <td>{team.stats.standings.wins}</td>
                            <td>{team.stats.standings.losses}</td>
                            <td>{team.stats.standings.overtimeLosses}</td>
                            <td>{team.stats.standings.points}</td>
                            <td>{team.stats.miscellaneous.goalsFor}</td>
                            <td>{team.stats.miscellaneous.goalsAgainst}</td>
                            <td>{team.stats.miscellaneous.goalsFor - team.stats.miscellaneous.goalsAgainst}</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
        )
      }



}
  export default DivisionStandings
