import React from "react"
import './index.css'


class Player extends React.Component {


    constructor(props) {
        super()
        this.state = {
            players: []
        }
    }

    async componentDidMount() {
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/player_stats_totals.json?team=TOR&rosterstatus=assigned-to-roster"
        const response = await fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        });
        const data = await response.json();
        this.setState({players: data.playerStatsTotals}) 
        console.log(this.state.players)


    }

    render() {
        return (
            <div id="players">
                {this.state.players.map((player, index) => (
                    <div id={player.player.id} class="player">
                        <img src={player.player.officialImageSrc == null ? "https://cms.nhl.bamgrid.com/images/headshots/current/168x168/skater.jpg" : player.player.officialImageSrc} alt=""
                        class="player-img"/>
                        <p class="playerName">{player.player.firstName} {player.player.lastName}</p>
                    </div>
                ))}
            </div>

        );
      }



}

  export default Player
