import React from "react"
import './index.css'


class Player extends React.Component {


    constructor(props) {
        super()
    }

    render() {
        let player = this.state.players.map(function(player,i) {
            if (player.player.currentRosterStatus === "ROSTER" || player.player.currentRosterStatus === "INJURY_LIST") {
                return(
                    <div key={player.player.id} className="player">
                        <img src={player.player.officialImageSrc == null ? "https://cms.nhl.bamgrid.com/images/headshots/current/168x168/skater.jpg" : player.player.officialImageSrc} alt=""
                        className="player-img"/>
                        <p className="player-name">{player.player.firstName} {player.player.lastName}</p>
                    </div>
                )
            }

            else {
                return ""
            }
        })
        return (
            <div id="players">
                {player}
            </div>

        );
      }



}

  export default Player
