import React from "react"
import './index.css'
import Players from "./Players.js"


class PlayerGroup extends React.Component {


    constructor(props) {
        super()
        this.state = {
            players: []
        }
    }

    async componentDidMount() {
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/player_stats_totals.json?team=TOR&rosterstatus=assigned-to-roster&position=" + this.props.position
        fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        })
        .then(res => res.json())
        .then(
            (data) => {
                this.setState({
                    players: data.playerStatsTotals
                })
            }
        )
    }

    render() {
        let position = ""
        if (this.props.position === "LW,RW,C") {
            position = "Forwards";
        }

        else if (this.props.position === "D") {
            position = "Defenseman"
        }

        else {
            position = "Goalies"
        }




        let player = this.state.players.map(function(player,i) {
            if (player.player.currentRosterStatus === "ROSTER" || player.player.currentRosterStatus === "INJURY_LIST") {
                return(
                    <Players player={player}/>
                )
            }

            else {
                return ""
            }
        })
        return (
            <div className="playerGroup">
                <h1>{position}</h1>
                {player}
            </div>

        );
      }


}

  export default PlayerGroup
