import React from "react"
import './index.css'


class PlayerDetails extends React.Component {


    constructor(props) {
        super()
        this.state = {
            playerInfo: [],
            playerStats: []
        }
    }

    async componentDidMount() {
        let { id } = this.props.match.params
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/player_stats_totals.json?player=" + id
        const response = await fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        });
        const data = await response.json();
        this.setState({playerInfo: data.playerStatsTotals[0].player, playerStats: data.playerStatsTotals.stats}) 
    }

    render() {
        return (
            <div className="player-container">
                <img src={this.state.playerInfo.officialImageSrc == null ? "https://cms.nhl.bamgrid.com/images/headshots/current/168x168/skater.jpg" : this.state.playerInfo.officialImageSrc} alt=""
                className="details-img"/>
                <p className="player-name">{this.state.playerInfo.firstName} {this.state.playerInfo.lastName}</p>
            </div>
        )
      }



}

  export default PlayerDetails
