import React from "react"
import './index.css'


class TeamGameStat extends React.Component {


    constructor(props) {
        super()
        this.state = {
            teamStat: [],
            standings: []
        }
    }

    async componentDidMount() {
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/2021-regular/standings.json?team="+this.props.id;
        console.log(url);
        fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        })
        .then(res => res.json())
        .then(
            (data) => {
                this.setState({
                    teamStat: data.teams[0].stats
                });
                this.setState({
                    standings: this.state.teamStat.standings
                })
            }
        );
    }

    render() {
        let wins = this.state.standings.wins
        let losses = this.state.standings.losses
        let otlosses = this.state.standings.overtimeLosses
        console.log(this.props.id)
        return (
            <p className="teamScore">{wins}-{losses}-{otlosses}</p>
        )
      }



}
  export default TeamGameStat
