import React from "react"
import './index.css'


class Game extends React.Component {


    constructor(props) {
        super()
        this.state = {
            nextGame: [],
            awayTeam: [],
            homeTeam: []
        }
    }

    async componentDidMount() {
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/2021-regular/games.json?team=TOR&status=unplayed,in-progress"
        const response = await fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        });
        const data = await response.json();
        this.setState({nextGame: data.games[0]}) 
        this.setState({
            awayTeam: this.state.nextGame.schedule.awayTeam,
            homeTeam: this.state.nextGame.schedule.homeTeam
        })

        if (this.state.nextGame.schedule.playedStatus === "LIVE") {
            const currentGameUrl = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/games/"+ this.state.nextGame.schedule.id + "/boxscore.json"
            const response = await fetch(currentGameUrl, { 
                headers: new Headers({
                  'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
                }), 
            });
            const newData = await response.json();
            this.setState({nextGame: newData.game}) 
        }

    }

    render() {
        return (
            <div className="game">
                <div id="awayTeam">
                    <img className="teamImg" src={process.env.PUBLIC_URL + '/assets/' + this.state.awayTeam.id + ".png"}/>
                    <p className="teamName">{this.state.awayTeam.abbreviation}</p>
                </div>
                <div id="homeTeam">
                <img className="teamImg" src={process.env.PUBLIC_URL + '/assets/' + this.state.homeTeam.id + ".png"}/>
                    <p className="teamName">{this.state.homeTeam.abbreviation}</p>
                </div>
            </div>
        );
      }



}

  export default Game
