import React from "react"
import './index.css'
import axios from 'axios';

import PlayerGroup from "./PlayerGroup.js"
import Game from "./Game.js"
import DivisionStandings from "./DivisionStandings.js"
import Loading from "./Loading.js"

class Home extends React.Component {


    constructor(props) {
        super()
        this.state = {
            teams: [],
            forwards: [],
            defense: [],
            goalie: [],
            game: [],
            liveGame: false
        }
    }

    async componentDidMount() {
        const headers = {'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS')}
        const divisionURL = "https://api.mysportsfeeds.com/v2.1/pull/nhl/2021-regular/standings.json?team=2,12,13,14,21,23,24";
        const forwardURL = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/player_stats_totals.json?team=TOR&rosterstatus=assigned-to-roster&position=LW,RW,C"
        const defenseURL = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/player_stats_totals.json?team=TOR&rosterstatus=assigned-to-roster&position=D"
        const goalieURL = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/player_stats_totals.json?team=TOR&rosterstatus=assigned-to-roster&position=G"
        const gameURL = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/games.json?status=unplayed,in-progress&team=TOR"

        const [divisionResponse, forwardResponse, defenseResponse, goalieResponse, gameResponse] = await Promise.all([
            axios.get(divisionURL, {headers: headers}),
            axios.get(forwardURL, {headers: headers}),
            axios.get(defenseURL, {headers: headers}),
            axios.get(goalieURL, {headers: headers}),
            axios.get(gameURL, {headers: headers})
        ])

        this.setState({
            teams: divisionResponse.data.teams,
            forwards: forwardResponse.data.playerStatsTotals, 
            defense: defenseResponse.data.playerStatsTotals,    
            goalie: goalieResponse.data.playerStatsTotals,
            game: gameResponse.data.games[0]
        })

        if (this.state.game.schedule.playedStatus === "LIVE") {
            const liveGameURL = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/games/" + this.state.game.games[0].schedule.id + "/boxscore.json";
            const liveGameResponse = await axios.get(liveGameURL, {headers: headers});
            this.setState({game: liveGameResponse.data, liveGame: true})
        }


    }

    render() {
        let timeLeft,awayTeam,awayTeamScore,homeTeam,homeTeamScore,subText = "";

        if (this.state.liveGame) {
            timeLeft = this.state.game.scoring.currentPeriodSecondsRemaining; 
            awayTeam = this.state.game.game.awayTeam,
            awayTeamScore = this.state.game.scoring.awayScoreTotal,
            homeTeam = this.state.game.game.homeTeam,
            homeTeamScore = this.state.game.scoring.homeScoreTotal,
            subText = "P" + this.state.game.scoring.currentPeriod + " (" + minutes + ":" + seconds + ")"
        }

        else {
            let date = new Date(this.state.game.schedule.startTime)
            let options = { weekday: 'long', month: 'long', day: 'numeric', hour: 'numeric', minute: '2-digit'};
            awayTeam = this.state.game.schedule.awayTeam
            awayTeamScore = this.state.game.score.awayScoreTotal
            homeTeam = this.state.game.schedule.homeTeam
            homeTeamScore = this.state.game.score.homeScoreTotal
            subText = date.toLocaleString('en-US', options)
        }

        return (
            <div>
                <Loading/>
                <Game liveGame={this.state.liveGame} nextGame={this.state.game} awayTeam={this.state.game.schedule.awayTeam}
                awayTeamScore={this.state.game.score.awayScoreTotal}
                homeTeam={this.state.game.schedule.homeTeam}
                homeTeamScore={this.state.game.score.homeScoreTotal}/>
                <DivisionStandings teams={this.state.teams}/>
                <PlayerGroup position="LW,RW,C" players={this.state.forwards}/>
                <PlayerGroup position="D" players={this.state.defense}/>
                <PlayerGroup position="G" players={this.state.goalie}/>
            </div>
        );
        }
}
  export default Home
