import React from "react"
import '../../index.css'
import Team from "./Team.js"


class Game extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            subText: this.props.subText
        }
    }

    async componentDidMount() {
        if (this.props.liveGame) {
            this.getSubText()
        }  
    }

    getSubText() {
        if (this.props.nextGame.scoring.currentPeriod === null) {
            this.setState({subText: "Intermission"})
        }

        else if (this.props.nextGame.scoring.currentPeriod > 3) {
            this.setState({subText: "Period: Overtime"})
        }

        if (this.props.nextGame.game.playedStatus !== "LIVE") {
            this.setState({subText: "Final Score"})
        }
    }

    render() {

        if (this.props.loaded) {
            return (
                <div className="game">
                    <Team type="awayTeam" id={this.props.awayTeam.id} abbreviation={this.props.awayTeam.abbreviation} score={this.props.awayTeamScore} awayTeamStat={this.props.awayTeamStat}/>
                    <Team type="homeTeam" id={this.props.homeTeam.id} abbreviation={this.props.homeTeam.abbreviation} score={this.props.homeTeamScore} homeTeamStat={this.props.shomeTeamStat}/>
                    <p className="subText">{this.state.subText}</p>
                </div>
            );
        }

        else {
            return (<div></div>)
        }


      }
}
  export default Game
