import React from "react"
import './index.css'


class PlayerGroup extends React.Component {


    constructor(props) {
        super()
        this.state = {
            players: []
        }
    }

    async componentDidMount() {
        const url = "https://api.mysportsfeeds.com/v2.1/pull/nhl/current/player_stats_totals.json?team=TOR&rosterstatus=assigned-to-roster&position=" + this.props.position
        const response = await fetch(url, { 
            headers: new Headers({
              'Authorization': 'Basic '+btoa('1e3895c7-87b5-4460-9249-8fc296:MYSPORTSFEEDS'), 
            }), 
        });
        const data = await response.json();
        this.setState({players: data.playerStatsTotals}) 
    }

    render() {
        let player = this.state.players.map(function(player,i) {
            if (player.player.currentRosterStatus === "ROSTER" || player.player.currentRosterStatus === "INJURY_LIST") {
                return(
                    <Player player={player}/>
                )
            }

            else {
                return ""
            }
        })
        return (
            <div id="players">
                {player}
            </div>

        );
      }


}

  export default PlayerGroup
